mapper.select:
select * from t_video where 1=1
and area=#{area}
order by id desc limit #{start},#{limit}

select *
from t_video_like
where videoId = #{videoId}
  and userId = #{userId}

select count(1)
from t_video_like
where videoId = #{videoId}

mapper.delete:
delete
from t_video_like
where videoId = #{videoId}
  and userId = #{userId}

mapper.select:
select count(1)
from t_video_collection
where videoId = #{videoId}

mapper.delete:
delete
from t_video_collection
where videoId = #{videoId}
  and userId = #{userId}

mapper.select:
select *
from t_video_collection
where videoId = #{videoId}
  and userId = #{userId}

select sum(amount)
from t_video_coin
where videoId = #{videoId}

mapper.update:
update t_video_coin
set amount     = #{amount},
    updateTime = #{updateTime}
where userId = #{userId}
  and videoId = #{videoId}

mapper.select:
select *
from t_video_coin
where videoId = #{videoId}
  and userId = #{userId}

select *
from t_video_comment
where videoId = #{videoId}
  and rootId is null
order by id desc
    limit #{start}, #{limit}

select * from t_video_comment where rootId in
#{rootId}
order by id

select
*
from
t_video_view
